import { createWebHistory, createRouter } from "vue-router";
import Home from "./components/Home.vue";
import Dashboard from "./layout/Dashboard.vue";
import Login from "./components/Login.vue";
import Register from "./components/Register.vue";
// lazy-loaded
const Profile = () => import("./components/Profile.vue")
const Admin = () => import("./components/Admin.vue")
const Guru = () => import("./components/Guru.vue")
const Walas = () => import("./components/Walas.vue")
const Gurulist = () => import("./components/DataGuru/Gurulist.vue")
const EditGuru = () => import("./components/DataGuru/EditGuru.vue")
const AddGuru = () => import("./components/DataGuru/AddGuru.vue")

const routes = [
  {
    path: "/",
    name: "dashboard",
    component: Dashboard,
    children: [
      {
        path: "/home",
        name: "home",
        // lazy-loaded
        component: Home,
      },
      {
        path: "/profile",
        name: "profile",
        // lazy-loaded
        component: Profile,
      },
      {
        path: "/admin",
        name: "admin",
        // lazy-loaded
        component: Admin,
      },
      {
        path: "/guru",
        name: "guru",
        // lazy-loaded
        component: Guru,
      },
      {
        path: "/walas",
        name: "walas",
        // lazy-loaded
        component: Walas,
      },

      {
        path: "/gurulist",
        name: "gurulist",
        // lazy-loaded
        component: Gurulist,
      },
      {
        path: "/gurulistedit/:id",
        name: "gurulistedit",
        // lazy-loaded
        component: EditGuru,
      },
      {
        path: "/gurulistadd",
        name: "gurulistadd",
        // lazy-loaded
        component: AddGuru,
      },
    ]
  },
  // {
  //   path: "/home",
  //   component: Home,
  // },
  {
    path: "/login",
    component: Login,
  },
  {
    path: "/register",
    component: Register,
  },

];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

router.beforeEach((to, from, next) => {
  // const publicPages = ['/login', '/register', '/home'];
  const publicPages = ['/login', 'register'];
  const authRequired = !publicPages.includes(to.path);
  const loggedIn = localStorage.getItem('user');

  // trying to access a restricted page + not logged in
  // redirect to login page
  if (authRequired && !loggedIn) {
    next('/login');
  } else {
    next();
  }
});

export default router;
