    }
    // guru() {
    //   if (this.form && this.form.role) {
    //     return this.form.role === "guru";
    //   }
    //   return false;
    // },
  },

  created() {
    this.getdata();
  },
  methods: {
    showModal() {
      this.statusmodal = false;
      this.resetForm();
      $("#modalmuncul").modal("show");
    },
    showModalEdit(user) {
      this.statusmodal = true;
      $("#modalmuncul").modal("show");
      this.getUserById(user);
    },
    getdata() {
      this.$Progress.start();
      User.getUser().then(response => {
        this.users = response.data;
        // $(function () {
