        //     .container()
        //     .appendTo("#myTable_wrapper .col-md-6:eq(0)");
        // });
        $(document).ready(function () {
          $("#myTable").dataTable();
        });
      }).catch(e => {
        console.log(e);
      });
      this.$Progress.finish();
    },
    getUserById(id) {
      User.getUserById(id).then(response => {
        this.form = response.data;
      }).catch(e => {
        console.log(e);
      });
    },
    validateData(data) {
      const schema = Joi.object({
        username: Joi.string().messages({
          "string.empty": "Username wajib diisi"
        }),
        tempat_lahir: Joi.string().messages({
          "string.empty": "Tempat Lahir wajib diisi"
        }),
        tanggal_lahir: Joi.date().messages({
          "date.base": "Format tanggal lahir tidak valid"
        }),
        jenis_kelamin: Joi.string().valid("L", "P").messages({
          "string.empty": "Jenis Kelamin wajib diisi"
        }),
        agama: Joi.string().messages({
          "string.empty": "Agama wajib diisi"
        }),
        alamat: Joi.string().messages({
          "string.empty": "Alamat wajib diisi"
        }),
        password: Joi.string().min(8).messages({
          "string.empty": "Password wajib diisi",
          "string.min": "Password yang anda masukkan kurang dari 8 karakter"
        }),
        confPassword: Joi.any().valid(Joi.ref("password")).messages({
          "string.empty": "Confirm Password wajib diisi",
          "any.only": "Confirm Password Tidak Cocok"
        }),
        role: Joi.string().valid("admin", "guru", "walas", "kabeng").messages({
          "string.empty": "Role wajib diisi"
        }),
        no_telp: Joi.string().messages({
          "string.empty": "Nomor Telepone wajib diisi"
        })
      });

      // const { error } = schema.validate(data);
      // return error ? error.details : null;
      const {
        error
      } = schema.validate(data, {
        abortEarly: false
      });
      return error ? error.details : null;
    },
    simpanData() {
      this.$Progress.start();
      this.loading = true;
      this.disabled = true;

      // Validasi data sebelum simpan
      const dataToCreate = {
        username: this.form.username,
        tempat_lahir: this.form.tempat_lahir,
        tanggal_lahir: this.form.tanggal_lahir,
        jenis_kelamin: this.form.jenis_kelamin,
        agama: this.form.agama,
        alamat: this.form.alamat,
        password: this.form.password,
        confPassword: this.form.confPassword,
        role: this.form.role,
        no_telp: this.form.no_telp
      };
      const validationError = this.validateData(dataToCreate);
      if (validationError) {
        // Setel pesan kesalahan ke variabel validationErrors
        this.validationErrors = validationError.reduce((errors, error) => {
          errors[error.context.key] = error.message;
          return errors;
        }, {});
        this.$Progress.fail();
        this.loading = false;
        this.disabled = false;
        return;
      }

      // Clear pesan kesalahan jika validasi berhasil
      this.validationErrors = null;
      User.createUser(this.form).then(response => {
        this.from = response.data;
        console.log(response.data);
        this.getdata();
        this.resetForm();
        $("#modalmuncul").modal("hide");
        const Toast = Swal.mixin({
          toast: true,
          position: "top-end",
          showConfirmButton: false,
          timer: 3000,
          timerProgressBar: true,
          didOpen: toast => {
            toast.addEventListener("mouseenter", Swal.stopTimer);
            toast.addEventListener("mouseleave", Swal.resumeTimer);
          }
        });
        Toast.fire({
          icon: "success",
          title: "Data Berhasil Tersimpan"
        });
        this.$Progress.finish();
        this.loading = false;
        this.disabled = false;
      }).catch(e => {
        this.$Progress.fail();
        this.loading = false;
        this.disabled = false;
        console.log(e);
        this.message = e.response && e.response.data && e.response.data.message || e.message || e.toString();
      });
    },
    updateData() {
      this.$Progress.start();
      this.loading = true;
      this.disabled = true;

      // Validasi data sebelum simpan
      const dataToCreate = {
        username: this.form.username,
        tempat_lahir: this.form.tempat_lahir,
        tanggal_lahir: this.form.tanggal_lahir,
        jenis_kelamin: this.form.jenis_kelamin,
        agama: this.form.agama,
        alamat: this.form.alamat,
        password: this.form.password,
        confPassword: this.form.confPassword,
        role: this.form.role,
        no_telp: this.form.no_telp
      };
      const validationError = this.validateData(dataToCreate);
      if (validationError) {
        // Setel pesan kesalahan ke variabel validationErrors
        this.validationErrors = validationError.reduce((errors, error) => {
          errors[error.context.key] = error.message;
          return errors;
        }, {});
        this.$Progress.fail();
        this.loading = false;
        this.disabled = false;
        return;
      }

      // Clear pesan kesalahan jika validasi berhasil
      this.validationErrors = null;
      User.updateUser(this.form.id, this.form).then(response => {
        this.from = response.data;
        console.log(response.data);
        this.getdata();
        this.resetForm();
        $("#modalmuncul").modal("hide");
        const Toast = Swal.mixin({
          toast: true,
          position: "top-end",
          showConfirmButton: false,
          timer: 3000,
          timerProgressBar: true,
          didOpen: toast => {
            toast.addEventListener("mouseenter", Swal.stopTimer);
            toast.addEventListener("mouseleave", Swal.resumeTimer);
          }
        });
        Toast.fire({
          icon: "success",
          title: "Data Berhasil Terupdate"
        });
        this.$Progress.finish();
        this.loading = false;
        this.disabled = false;
      }).catch(e => {
        this.$Progress.fail();
        this.loading = false;
        this.disabled = false;
        console.log(e);
      });
    },
    deleteData(id) {
      Swal.fire({
        title: "Anda Yakin Ingin Menghapus Data Ini ?",
        text: "Klik Batal Untuk Membatalkan Penghapusan",
        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "Hapus"
      }).then(result => {
        if (result.value) {
          User.deleteUser(id).then(response => {
            Swal.fire("Terhapus", "Data Anda Sudah Terhapus", "success");
            console.log(response.data);
            this.getdata();
          }).catch(() => {
            Swal.fire("Gagal", "Data Gagal Terhapus", "warning");
          });
        }
      });
    },
    resetForm() {
      this.form.username = "", this.form.email = "", this.form.password = "", this.form.confPassword = "", this.form.role = "", this.form.tempat_lahir = "", this.form.tanggal_lahir = "", this.form.jenis_kelamin = "", this.form.agama = "", this.form.alamat = "", this.form.almt_kel = "", this.form.almt_kec = "", this.form.almt_kab = "", this.form.almt_prov = "", this.form.no_telp = "", this.validationErrors = "";
    }
  }
};
