    getNilaiNP(siswaId, guruId, jenis, i) {
      const nilai = this.npData.find(item => item.siswaId === siswaId && item.guruId === guruId);
      if (nilai) {
        if (jenis === "uts" || jenis === "uas") {
          return nilai[jenis] ? nilai[jenis] : "-";
        } else {
          return nilai[jenis + i] ? nilai[jenis + i] : "-";
        }
      } else {
        return "-";
      }
    }
